import axios from 'axios';

const API_BASE_URL = 'http://localhost:8000/api';

const api = axios.create({
  baseURL: API_BASE_URL,
  withCredentials: true, // Importante para sessions
});

// Armazenar CSRF token globalmente
let csrfToken = null;

export const setCSRFToken = (token) => {
  csrfToken = token;
  // Também salva no localStorage para persistência
  if (token) {
    localStorage.setItem('csrfToken', token);
  } else {
    localStorage.removeItem('csrfToken');
  }
};

export const getCSRFToken = () => {
  return csrfToken || localStorage.getItem('csrfToken');
};

// Função para obter um NOVO CSRF token do servidor
export const fetchCSRFToken = async () => {
  try {
    const response = await api.get('/csrf/');
    if (response.data.csrfToken) {
      setCSRFToken(response.data.csrfToken);
    }
    return response.data.csrfToken;
  } catch (error) {
    console.error('Erro ao obter CSRF token:', error);
    return null;
  }
};

// Interceptor para incluir CSRF token em todas as requisições
api.interceptors.request.use(async (config) => {
  // Para requisições que não são GET, inclui CSRF token
  if (['post', 'put', 'patch', 'delete'].includes(config.method.toLowerCase())) {
    let token = getCSRFToken();

    // Se não tem token, tenta obter um novo
    if (!token) {
      token = await fetchCSRFToken();
    }

    if (token) {
      config.headers['X-CSRFToken'] = token;
    }
  }
  return config;
});

// Interceptor para tratar erros de autenticação e CSRF
api.interceptors.response.use(
  (response) => response,
  async (error) => {
    if (error.response?.status === 403 && error.response.data?.detail?.includes('CSRF')) {
      // CSRF token inválido - tenta obter um novo e refazer a requisição
      console.log('CSRF token inválido, obtendo novo token...');
      const newToken = await fetchCSRFToken();

      if (newToken && error.config) {
        // Atualiza o header com o novo token e refaz a requisição
        error.config.headers['X-CSRFToken'] = newToken;
        return api.request(error.config);
      }
    }

    if (error.response?.status === 401) {
      // Redirecionar para login se não autenticado
      window.location.href = '/login';
    }

    return Promise.reject(error);
  }
);

export default api;
